import axios from 'axios';

class Calls {
    getRandomActivity() {
        axios.get('http://www.boredapi.com/api/activity')
            .then((res) => {
                return res.data;
            }, (err) => {
                console.log(err);
                return err;
            });
    }

    getNumberFact(number, type) {
        if (type === '') {
            type = 'trivia';
        }
        axios.get('http://numbersapi.com/' + number + '/' + type)
            .then((res) => { console.log(res) })
    }
}

export default new Calls();
