import 'date-fns';
import React, { useEffect, useState } from 'react';
import DateFnsUtils from '@date-io/date-fns';

import {
    Grid
} from '@material-ui/core';


import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker
} from '@material-ui/pickers';

import './css/home.css';

import calls from '../assets/js/calls';
import BoredActivity from './BoredActivity';
import NumberFact from './NumberFact';

export default function Home(props) {
    const [state, setState] = useState(0);
    const [number, setNumber] = useState(null);
    const [activity, setActivity] = useState(null);

    const [selectedDate, setSelectedDate] = useState(new Date('now'));

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    const getActivity = () => {

    }

    const getFact = () => {

    }

    return (
        <Grid container justify='center' alignItems='center'>
            <Grid item xs={4} id='filter_container'>
                <h1 id='title'>I am bored.</h1>
                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                    <KeyboardDatePicker
                        margin="normal"
                        id="date-picker-dialog"
                        label="Date picker dialog"
                        format="MM/dd/yyyy"
                        value={selectedDate}
                        onChange={handleDateChange}
                        KeyboardButtonProps={{
                            'aria-label': 'change date',
                        }}
                    />
                </MuiPickersUtilsProvider>
            </Grid>
            {state === 1 ? (
                <NumberFact number={number} />
            ) : (null)}
            {state === 2 ? (
                <BoredActivity activity={activity} />
            ) : (null)}
        </Grid>
    )
}
