import React, { useEffect, useState } from 'react';

import {
    Grid,
    TextField,
    Button,
    FormControl,
    InputLabel,
    Select,
    MenuItem
} from '@material-ui/core';

import './css/home.css';

import calls from '../assets/js/calls';
import BoredActivity from './BoredActivity';
import NumberFact from './NumberFact';

export default function Home(props) {
    const [state, setState] = useState(0);
    const [number, setNumber] = useState(null);
    const [activity, setActivity] = useState(null);
    const [date, setDate] = useState(null);
    const [type, setType] = useState(null);

    const handleNumber = (e) => {
        setNumber(e.target.value)
    }

    const handleType = (e) => {
        setType(e.target.value)
    }

    const getActivity = () => {

    }

    const getFact = () => {

    }

    return (
        <Grid container justify='center' alignItems='center'>
            <Grid item xs={4} id='filter_container'>
                <h1 id='title'>I am bored. I want to know a fact for ...</h1>
                <FormControl style={{ width: '100%' }}>
                    <InputLabel id='number'>Number</InputLabel>
                    <TextField
                        labelId='number'
                        type='number'
                        value={number}
                        onChange={(e) => { handleNumber(e) }}
                    />
                </FormControl>
                <FormControl>
                    <InputLabel id="type">Type</InputLabel>
                    <Select
                        labelId="type"
                        value={type}
                        onChange={(e) => { handleType(e) }}
                    >
                        <MenuItem value={'Math'}>Math</MenuItem>
                        <MenuItem value={'Trivia'}>Trivia</MenuItem>
                        <MenuItem value={'Year'}>Year</MenuItem>
                    </Select>
                </FormControl>
                <Button variant='contained'>a number</Button>
                <FormControl>
                    <TextField
                        label='date'
                        type="date"
                        value={date}
                        InputLabelProps={{
                            shrink: true,
                        }}
                    />
                </FormControl>
                <Button variant='contained'>a date</Button>
            </Grid>
            {state === 1 ? (
                <NumberFact number={number} />
            ) : (null)}
            {state === 2 ? (
                <BoredActivity activity={activity} />
            ) : (null)}
        </Grid>
    )
}
