import axios from 'axios';

class Calls {
    getRandomActivity() {
        axios.get('http://www.boredapi.com/api/activity')
            .then((res) => {
                return res.data;
            }, (err) => {
                console.log(err);
                return err;
            });
    }

    getNumberFact(number, type) {
        return new Promise((res, rej) => {
            axios.get('http://numbersapi.com/' + number + '/' + type)
                .then(
                    (res) => {
                        res(res.data)
                    },
                    (err) => {
                        rej(err)
                    }
                )
        });
    }
}

export default new Calls();
