import React, { useEffect, useState } from 'react';

import {
    Grid,
    TextField,
    FormControl,
    InputLabel,
    Select,
    MenuItem
} from '@material-ui/core';

import './css/home.css';

import calls from '../assets/js/calls';
import BoredActivity from './BoredActivity';
import NumberFact from './NumberFact';

export default function Home(props) {
    const [state, setState] = useState(0);
    const [activity, setActivity] = useState(null);
    const [fact, setFact] = useState(null);
    const [number, setNumber] = useState(42);
    const [date, setDate] = useState(null);
    const [type, setType] = useState('trivia');

    const handleNumber = (e) => {
        setNumber(e.target.value)
    }

    const handleType = (e) => {
        setType(e.target.value)
    }

    // const getActivity = () => {
    //     const activity = calls.getRandomActivity();
    // }

    const getFact = (number, type) => {
        const fact = calls.getNumberFact(number, type);
        setFact(fact);
    }

    return (
        <Grid container justify='center' alignItems='center'>
            <Grid item xs={4} id='filter_container'>
                <h1 id='title'>I am bored. I want to know a fact for ...</h1>
                <FormControl className='input'>
                    <TextField
                        label='Number'
                        type='text'
                        value={number}
                        onChange={(e) => { handleNumber(e) }}
                    />
                </FormControl>
                <FormControl className='input'>
                    <InputLabel id="type">Type</InputLabel>
                    <Select
                        labelId="type"
                        value={type}
                        onChange={(e) => { handleType(e) }}
                    >
                        <MenuItem value={'Math'}>Math</MenuItem>
                        <MenuItem value={'Trivia'}>Trivia</MenuItem>
                        <MenuItem value={'Year'}>Year</MenuItem>
                    </Select>
                </FormControl>
                <Button className='button' variant='contained'>a number</Button>
                <FormControl className='input'>
                    <TextField
                        label='Date'
                        type="date"
                        value={date}
                        InputLabelProps={{
                            shrink: true,
                        }}
                    />
                </FormControl>
                <Button className='button' variant='contained'>a date</Button>
                <Button variant='contained'>Give me something to do</Button>
            </Grid>
            {state === 1 ? (
                <NumberFact number={number} />
            ) : (null)}
            {state === 2 ? (
                <BoredActivity activity={activity} />
            ) : (null)}
        </Grid>
    )
}
